import React from 'react';
import { useFormik } from 'formik';
import { Box, Button, TextField } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { object, string } from 'yup';
import { Image_Post } from '../../../Services/ImageService';

interface ImagePostProps {
  imagePost: Image_Post;
  submitActionHandler: (values: Image_Post) => void;
}

const ImagePostForm: React.FC<ImagePostProps> = ({ imagePost, submitActionHandler }) => {
  const navigate = useNavigate();

  const formik = useFormik({
    initialValues: {
        id: imagePost.id,
        image: imagePost ? imagePost.image : '',
        description: imagePost ? imagePost.description : '',
        likes: imagePost ? imagePost.likes : 0,
        author: {
            id: '',
            email: '',
            firstName: '',
            lastName: '',
            roles: []
        }
    },
    validationSchema: object({
      image: string().required(),
      description: string().required().min(2).max(100),
      likes: string().required().matches(/^\d+$/, 'Likes must be a number'),
    }),
    onSubmit: (values: Image_Post) => {
      submitActionHandler(values);
    },
    enableReinitialize: true,
  });

  return (
    <>
      <form onSubmit={formik.handleSubmit}>
        <Box sx={{ paddingTop: '15px' }}>
          <TextField
            id='image'
            label='Image URL'
            variant='outlined'
            sx={{ paddingRight: '10px' }}
            onBlur={formik.handleBlur}
            onChange={formik.handleChange}
            error={Boolean(formik.touched.image && formik.errors.image)}
            value={formik.values.image}
          />
          {formik.errors.image && formik.touched.image ? (
            <div style={{ color: 'red' }}>{formik.errors.image}</div>
          ) : null}
          <TextField
            id='description'
            label='Description'
            variant='outlined'
            sx={{ paddingRight: '10px' }}
            onBlur={formik.handleBlur}
            onChange={formik.handleChange}
            error={Boolean(formik.touched.description && formik.errors.description)}
            value={formik.values.description}
          />
          {formik.errors.description && formik.touched.description ? (
            <div style={{ color: 'red' }}>{formik.errors.description}</div>
          ) : null}
          <TextField
            id='likes'
            label='Likes'
            variant='outlined'
            sx={{ paddingRight: '10px' }}
            onBlur={formik.handleBlur}
            onChange={formik.handleChange}
            error={Boolean(formik.touched.likes && formik.errors.likes)}
            value={formik.values.likes}
          />
          {formik.errors.likes && formik.touched.likes ? (
            <div style={{ color: 'red' }}>{formik.errors.likes}</div>
          ) : null}
        </Box>
        <div>
          <Button
            sx={{ marginTop: '15px', marginRight: '10px' }}
            variant='contained'
            color='success'
            type='submit'
            disabled={!(formik.dirty && formik.isValid)}
          >
            {imagePost.id ? 'Save' : 'Add'}
          </Button>
          <Button
            sx={{ marginTop: '15px' }}
            variant='contained'
            color='error'
            onClick={() => {
              navigate('/image_posts');
            }}
          >
            Cancel
          </Button>
        </div>
      </form>
    </>
  );
};

export default ImagePostForm;
