import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Image_Post } from '../../../Services/ImageService';
import ImageForm from '../../molecules/UserForm/ImageForm';

const ImagePage = () => {
  const navigate = useNavigate();
  const [image, setImage] = useState<Image_Post>({
    image: '', // Initialize with empty values
    description: '',
  });

  const submitActionHandler = (values: Image_Post) => {
    // Create a new FormData object to handle file upload
    const formData = new FormData();
    formData.append('image', values.image);
    formData.append('description', values.description);

    // Make a POST request to your backend endpoint for image creation
    fetch('/api/your-image-endpoint', {
      method: 'POST',
      body: formData,
    })
      .then((response) => {
        if (response.ok) {
          // Image successfully uploaded, navigate to the desired page
          navigate('/images');
        } else {
          // Handle error cases here
          console.error('Image upload failed.');
        }
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  return <ImageForm image={image} submitActionHandler={submitActionHandler} />;
};

export default ImagePage;
