import React from 'react';
import { Box, Button, TextField } from '@mui/material';
import { useFormik } from 'formik';
import { object, string } from 'yup';
import { Image_Post } from '../../../Services/ImageService';

interface ImageProps {
  image: Image_Post;
  submitActionHandler: (values: Image_Post) => void;
}

const ImageForm = ({ image, submitActionHandler }: ImageProps) => {
  const formik = useFormik({
    initialValues: {
        id: 0,
        image: "",
        description: "",
        likes: 0,
        author: {
          id: "",
          email: "",
          firstName: "",
          lastName: "",
          roles: []
      }
    },
    validationSchema: object({
      // Add validation rules as needed
      description: string().required('Description is required').max(500),
    }),
    onSubmit: (values: Image_Post) => {
      submitActionHandler(values);
    },
  });

  return (
    <form onSubmit={formik.handleSubmit}>
      <Box sx={{ paddingTop: '15px' }}>
        {/* Input for image file */}
        <input
  type="file"
  name="image"
  accept="image/*"
  onChange={(event) => {
    console.log('Selected file:', event.currentTarget.files[0]);
    formik.setFieldValue('image', event.currentTarget.files[0]);
  }}
/>

        />

        {/* Input for image description */}
        <TextField
          id='description'
          name='description'
          label='Description'
          variant='outlined'
          fullWidth
          multiline
          rows={4}
          value={formik.values.description}
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          error={formik.touched.description && Boolean(formik.errors.description)}
          helperText={formik.touched.description && formik.errors.description}
        />
      </Box>

      <div>
        <Button
          sx={{ marginTop: '15px', marginRight: '10px' }}
          variant='contained'
          color='success'
          type='submit'
          disabled={!formik.isValid}
        >
          Add Image
        </Button>
      </div>
    </form>
  );
};

export default ImageForm;
