    <>
      {imagePosts.map((imagePost) => (
        <div key={imagePost.id}>
          <Card sx={{ minWidth: 275 }}>
            <CardContent>
              {imagePost.description} Likes: {imagePost.likes}
              <CardActions>
                <Button
                  size='small'
                  color='primary'
                  variant='contained'
                  onClick={() => handleEdit(imagePost.id)}
                >
                  Edit
                </Button>
                <Button
                  size='small'
                  color='error'
                  variant='contained'
                  onClick={() => handleDelete(imagePost.id)}
                >
                  Delete
                </Button>
              </CardActions>
            </CardContent>
          </Card>
        </div>
      ))}
      <Button
        size='small'
        color='success'
        variant='contained'
        onClick={handleAdd}
      >
        Add
      </Button>
    </>
  );
};

export default ImagePostTable;
