import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import ImageService, { Image_Post } from '../../../Services/ImageService';
import ImagePostForm from './ImageForm';


const ImagePage: React.FC = () => {
  const navigate = useNavigate();
  const { imageId } = useParams();
  const [imagePost, setImagePost] = useState<Image_Post>({
    id: 0,
    image: '',
    description: '',
    likes: 0,
    
  });

  useEffect(() => {
    if (imageId) {
      ImageService.getImage().then((res) => {
        setImagePost(res);
      });
    }
  }, [imagePost]);

  const submitActionHandler = (values: Image_Post) => {
    if (imagePost !== undefined) {
      ImageService.updateImagePost(values).then(() => {
        navigate('../image_posts');
      });
    } else {
      ImageService.addImage(values).then(() => {
        navigate('/image_posts');
      });
    }
  };

  return <ImagePostForm imagePost={imagePost} submitActionHandler={submitActionHandler} />;
};

export default ImagePage;
