import { AxiosInstance } from "axios";
import { User } from "../types/models/User.model";
import api from "../config/Api";

type AddImageRequest = {
  image: string;
  description: string;
 
};

export type Image_Post = {
  id: number;
  image: string;
  description: string;
  likes: number;
  author: User;
};


const ImageService = {
  getImageById: async (id: number) => {
    const data = await api.get(`/imagepost/${id}`);
    console.log(data);
    return data.data;
    
  },
  getAllImages: async () => {
    const data = await api.get<Image_Post[]>("/imagepost/getall");
    return data.data;
  },
  createImage: async (params: AddImageRequest) => {
    const res = await api.post("/imagepost/create/", params);
    if (res && res.status === 200) {
      console.log("image successfully created");
    }
  },
  removeImage: async (id: number) => {
    const res = await api.delete(`/imagepost/${id}`);
    if (res && res.status === 200) {
      console.log("image successfully deleted");
    }
  },
  updateImage: async (params: Image_Post) => {
    const res = await api.put(`/imagepost/${params.image}`, {...params, "Release Date": params.description});
    if (res && res.status === 200) {
      console.log("image successfully updated");
    }
  },
};

export default ImageService;
