import { useNavigate, useParams } from "react-router-dom";
import { User } from "../../../types/models/User.model";
import UserService from "../../../Services/UserService";
import UserForm from "../../molecules/UserForm/UserForm";
import { useEffect, useState } from "react";
import ImageService, { Image_Post } from "../../../Services/ImageService";
import UserPage from "./UserPage";
import ImageForm from "../../molecules/UserForm/ImageForm";

const ImagePage = () => {
  const navigate = useNavigate();
  const { imageId } = useParams();
  const [image, setImage] = useState<Image_Post>({
    id: 0,
    image: "",
    description: "",
    likes: 0,
    author: {
      id: "",
      email: "",
      firstName: "",
      lastName: "",
      roles: [],
    },
  });

  useEffect(() => {
    return () => {
      if (imageId) {
        ImageService.getImage(imageId).then((res) => {
          return setImage(res);
        });
      }
    };
  }, [imageId]);

  const submitActionHandler = (values: Image_Post) => {
      ImageService.addImage(values).then(() => {
        navigate("/create");
      });
    
  };

  return <ImageForm image={image} submitActionHandler={submitActionHandler} />;
};
export default ImagePage;
