import React from 'react';
import { Box, Button, TextField } from '@mui/material';
import { useFormik } from 'formik';
import { object, string } from 'yup';
import { Image_Post } from '../../../Services/ImageService';

interface ImageProps {
  image: Image_Post;
  submitActionHandler: (values: Image_Post) => void;
}

const ImageForm = ({ image, submitActionHandler }: ImageProps) => {
  const formik = useFormik({
    initialValues: {
      image: '', // Initialize with an empty string
      description: image.description || '', // Preserve existing description
    },
    validationSchema: object({
      // Add validation rules as needed
      description: string().required('Description is required'),
    }),
    onSubmit: (values: Image_Post) => {
      submitActionHandler(values);
    },
  });

  return (
    <form onSubmit={formik.handleSubmit}>
      <Box sx={{ paddingTop: '15px' }}>
        {/* Input for image file */}
        <input
          type="file"
          name="image"
          accept="image/*" // Limit file selection to image types
          onChange={(event) => {
            formik.setFieldValue('image', event.currentTarget.files[0]);
          }}
        />
        {/* Display validation error for description */}
        {formik.touched.description && formik.errors.description && (
          <div style={{ color: 'red' }}>{formik.errors.description}</div>
        )}
      </Box>
      <div>
        <Button
          sx={{ marginTop: '15px', marginRight: '10px' }}
          variant='contained'
          color='success'
          type='submit'
          disabled={!formik.isValid}
        >
          Add Image
        </Button>
      </div>
    </form>
  );
};

export default ImageForm;
