import api from '../config/Api';
import { User } from '../types/models/User.model';
export type Image_Post = {
  id: number;
  image: string;
  description: string;
  likes: number;
  author: User;
};
const ImageService = {
  getImage: async (userID: string): Promise<Image_Post> => {
    const { data } = await api.get<User>(`/imagepost/${userID}`);
    return data;
  },

  updateImage: (user: User) => {
    return api.put(`/imagepost/${user.id}`, user);
  },

  addImage: (user: User) => {
    return api.post('/imagepost/create', user).then((res) => {
      return res.data;
    });
  },

  getAllImages: async () => {
    try {
      const response = await api.get("/imagepost/getAll");
      return response.data;
    } catch (error) {
      console.error("ERRORRRRRRRR", error);
      return [];
    }
  },

  deleteUser: (id: string) => {
    return api.delete(`/imageposty/${id}`);
  },
};

export default ImageService;
