import { AxiosInstance } from "axios";
import { User } from "../types/models/User.model";
import api from "../config/Api";
export type Image_Post = {
  id: number;
  image: string;
  description: string;
  likes: number;
  author: User;
};
const ImageService = {
  getImage: async (imageID: string): Promise<User> => {
    const { data } = await api.get<User>(`/imagepost/${imageID}`);
    return data;
  },
  getAllImages: async () => {
    try {
      const response = await api.get("/imagepost/getAll");
      return response.data;
    } catch (error) {
      console.error("ERRORRRRRRRR", error);
      return [];
    }
  },
  addImage: (image: Image_Post) => {
    return api.post('/imagepost/create', image).then((res) => {
      return res.data;
    });
